--
-- Create procedure `Proc_ConvertStringToTable`
--
CREATE PROCEDURE Proc_ConvertStringToTable(IN $lstValue text, IN $SeparateCharacter char(1))
    SQL SECURITY INVOKER
BEGIN
    DECLARE
        $strJson longtext;
    SET
        $strJson = CONCAT('[{"a":"', REPLACE($lstValue, $SeparateCharacter, '"},{"a":"'), '"}]');
    DROP
        TEMPORARY TABLE IF EXISTS tmpListConvert;
    CREATE
        TEMPORARY TABLE tmpListConvert
    (
        value varchar(500)
    ) COLLATE utf8mb4_general_ci;

    IF
        JSON_VALID($strJson) = 1 THEN
        INSERT INTO tmpListConvert
        SELECT *
        FROM JSON_TABLE($strJson, "$[*]" COLUMNS (
      a varchar(500) PATH "$.a"
      )
         ) AS tt;
    END IF;
END
$$

--
-- Create procedure `Proc_GetProductRelationOrder`
--
CREATE PROCEDURE Proc_GetProductRelationOrder(IN $ListProductId text)
    SQL SECURITY INVOKER
    COMMENT 'Lấy danh sách sản phẩm liên quan'
BEGIN
    CALL Proc_ConvertStringToTable($ListProductId, ',');

-- Lấy danh sách đơn hàng có sản phẩm đầu vào
    DROP
        TEMPORARY TABLE IF EXISTS tmpListProductOrder;
    CREATE
        TEMPORARY TABLE tmpListProductOrder
    SELECT DISTINCT po.product_id,
                    po.relation_id,
                    po.count_relation
    FROM product_relation po
             INNER JOIN tmpListConvert tlp
                        ON po.product_id = tlp.value;

-- Lấy các sản phẩm khác cùng đơn với sản đầu vào
    DROP
        TEMPORARY TABLE IF EXISTS tmpListProductOrderRelation;
    CREATE
        TEMPORARY TABLE tmpListProductOrderRelation
    SELECT p.product_id,
           p.product_code,
           p.product_name,
           p.description,
           p.created_date,
           p.outstanding,
           tlpo.count_relation AS countProduct
    FROM tmpListProductOrder tlpo
             INNER JOIN product p
                        ON tlpo.relation_id = p.product_id
    WHERE $ListProductId NOT LIKE CONCAT('%', tlpo.relation_id, '%')
    ORDER BY countProduct DESC
    LIMIT 20 OFFSET 0;

-- Lấy thông tin sản phẩm
    DROP
        TEMPORARY TABLE IF EXISTS result1;
    CREATE
        TEMPORARY TABLE result1 COLLATE utf8mb4_general_ci
    SELECT pd.product_id,
           COUNT(pd.product_detail_id)                        AS count_detail, -- số sản phẩm trong 1 loại sp
           GROUP_CONCAT(IFNULL(pd.img_url, '') SEPARATOR ';') AS img_url,
           MAX(pd.sale_price)                                 AS sale_price_max,
           MIN(pd.sale_price)                                 AS sale_price_min,
           MAX(pd.sale_price_old)                             AS sale_price_old,
           MAX(pd.product_discount)                           AS product_discount,
           p.product_code,
           p.product_name,
           p.description,
           p.created_date,
           p.outstanding,
           p.countProduct
    FROM product_detail pd
             JOIN tmpListProductOrderRelation p
                  ON pd.product_id = p.product_id
    GROUP BY pd.product_id,
             p.product_code,
             p.product_name,
             p.description,
             p.created_date,
             p.outstanding,
             p.countProduct;

-- Lấy trung bình cộng số sao đánh giá
    DROP
        TEMPORARY TABLE IF EXISTS result2;
    CREATE
        TEMPORARY TABLE result2 COLLATE utf8mb4_general_ci (SELECT r1.*,
                                                                   AVG(IFNULL(c.rate, 0)) AS rate -- trung bình cộng số sao đánh giá
                                                            FROM result1 r1
                                                                     LEFT JOIN comment c
                                                                               ON r1.product_id = c.product_id
                                                            GROUP BY r1.product_id,
                                                                     r1.product_code,
                                                                     r1.product_name,
                                                                     r1.description,
                                                                     r1.created_date,
                                                                     r1.product_discount,
                                                                     r1.sale_price_old,
                                                                     r1.sale_price_min,
                                                                     r1.sale_price_max,
                                                                     r1.img_url,
                                                                     r1.outstanding,
                                                                     r1.count_detail,
                                                                     r1.countProduct);

    -- Lấy số sp đã bán
    DROP
        TEMPORARY TABLE IF EXISTS result3;
    CREATE
        TEMPORARY TABLE result3 COLLATE utf8mb4_general_ci
    SELECT r2.*,
           SUM(IFNULL(po.quantity, 0)) AS count_order -- Số sp đã bán: khác 4 và 7(khác đã hủy đơn và khác đơn đã đc hoàn trở lại)
    FROM result2 r2
             INNER JOIN product_order po
                        ON r2.product_id = po.product_id
             INNER JOIN `order` o
                        ON po.order_id = o.order_id
    WHERE o.status <> 4
        AND o.status <> 7
       OR o.status IS NULL
    GROUP BY r2.product_id,
             r2.product_code,
             r2.product_name,
             r2.description,
             r2.created_date,
             r2.product_discount,
             r2.sale_price_old,
             r2.sale_price_min,
             r2.sale_price_max,
             r2.img_url,
             r2.outstanding,
             r2.count_detail,
             r2.rate,
             r2.countProduct;
    SELECT *
    FROM result3 r
    ORDER BY r.countProduct DESC
    LIMIT 20 OFFSET 0;
END
$$

--
-- Create procedure `Proc_GetProductHome`
--
CREATE PROCEDURE Proc_GetProductHome(IN $keyword varchar(255), IN $rating int, IN $fromAmount decimal(10, 0),
                                     IN $toAmount decimal(10, 0), IN $category text,
                                     IN $sort int, -- sắp xếp:  1: Mới nhất, 2: Báy chạy, 3: Giá Thấp đến cao, 4: Giá Cao đến thấp
                                     IN $page int, -- sắp xếp:  1: Mới nhất, 2: Báy chạy, 3: Giá Thấp đến cao, 4: Giá Cao đến thấp
                                     IN $pageSize int)
    SQL SECURITY INVOKER
    COMMENT 'Lấy danh sách sản phẩm trang home page có tìm kiếm, sắp xếp,...'
BEGIN
    DECLARE
        $totalRecord int;
    DECLARE
        $offset int;
    SET
        $offset = $page * $pageSize;

    DROP
        TEMPORARY TABLE IF EXISTS result1;
    CREATE
        TEMPORARY TABLE result1 COLLATE utf8mb4_general_ci
    SELECT pd.product_id,
           COUNT(pd.product_detail_id)                        AS count_detail, -- số sản phẩm trong 1 loại sp
           GROUP_CONCAT(IFNULL(pd.img_url, '') SEPARATOR ';') AS img_url,
           MAX(pd.sale_price)                                 AS sale_price_max,
           MIN(pd.sale_price)                                 AS sale_price_min,
           MAX(pd.sale_price_old)                             AS sale_price_old,
           MAX(pd.product_discount)                           AS product_discount,
           p.product_code,
           p.product_name,
           p.description,
           p.created_date,
           p.outstanding
    FROM product_detail pd
             LEFT JOIN product p
                       ON pd.product_id = p.product_id
    WHERE p.status = 1
    GROUP BY pd.product_id,
             p.product_code,
             p.product_name,
             p.description,
             p.created_date,
             p.outstanding;

-- Lấy số sp đã bán của sp
    DROP
        TEMPORARY TABLE IF EXISTS result2;
    CREATE
        TEMPORARY TABLE result2 COLLATE utf8mb4_general_ci (SELECT r1.*,
                                                                   AVG(IFNULL(c.rate, 0)) AS rate -- trung bình cộng số sao đánh giá
                                                            FROM result1 r1
                                                                     LEFT JOIN comment c
                                                                               ON r1.product_id = c.product_id
                                                            GROUP BY r1.product_id,
                                                                     r1.product_code,
                                                                     r1.product_name,
                                                                     r1.description,
                                                                     r1.created_date,
                                                                     r1.product_discount,
                                                                     r1.sale_price_old,
                                                                     r1.sale_price_min,
                                                                     r1.sale_price_max,
                                                                     r1.img_url,
                                                                     r1.outstanding,
                                                                     r1.count_detail);

    -- Lấy số sao đánh giá của sp
    DROP
        TEMPORARY TABLE IF EXISTS result3;
    CREATE
        TEMPORARY TABLE result3 COLLATE utf8mb4_general_ci
    SELECT r2.*,
           SUM(IFNULL(po.quantity, 0)) AS count_order -- Số sp đã bán: khác 4(khác đã hủy đơn)
    FROM result2 r2
             LEFT JOIN product_order po
                       ON r2.product_id = po.product_id
             LEFT JOIN `order` o
                       ON po.order_id = o.order_id
                           AND (o.status <> 4
                               OR o.status IS NULL)
         --   WHERE o.status <> 4 OR o.status IS NULL
    GROUP BY r2.product_id,
             r2.product_code,
             r2.product_name,
             r2.description,
             r2.created_date,
             r2.product_discount,
             r2.sale_price_old,
             r2.sale_price_min,
             r2.sale_price_max,
             r2.img_url,
             r2.outstanding,
             r2.count_detail,
             r2.rate;
-- Lấy danh sách sp theo nhóm Danh mục
    IF
            $category IS NOT NULL
            AND $category <> '' THEN
        CALL Proc_ConvertStringToTable($category, ',');
        DROP
            TEMPORARY TABLE IF EXISTS tmpProductCategory;
        CREATE
            TEMPORARY TABLE tmpProductCategory
        SELECT DISTINCT pc.product_id
        FROM product_category pc
                 INNER JOIN tmpListConvert tlc
                            ON pc.category_id = tlc.value;

-- Lấy tổng số bản ghi
        SELECT COUNT(*)
        INTO $totalRecord
        FROM result3 r
                 INNER JOIN tmpProductCategory tdc
                            ON r.product_id = tdc.product_id
        WHERE r.product_name LIKE CONCAT('%', IFNULL($keyword, ''), '%')
          AND ($fromAmount IS NULL
            OR r.sale_price_max >= $fromAmount)
          AND ($toAmount IS NULL
            OR r.sale_price_min <= $toAmount)
          AND ($rating IS NULL
            OR r.rate >= $rating);
-- Lấy danh sách sản phẩm
        SELECT *,
               $totalRecord AS totalRecord
        FROM result3 r
                 INNER JOIN tmpProductCategory tdc
                            ON r.product_id = tdc.product_id
        WHERE r.product_name LIKE CONCAT('%', IFNULL($keyword, ''), '%')
          AND ($fromAmount IS NULL
            OR r.sale_price_max >= $fromAmount)
          AND ($toAmount IS NULL
            OR r.sale_price_min <= $toAmount)
          AND ($rating IS NULL
            OR r.rate >= $rating)
        ORDER BY CASE WHEN $sort = 1 THEN created_date END DESC,
                 CASE WHEN $sort = 2 THEN count_order END DESC,
                 CASE WHEN $sort = 4 THEN sale_price_max END DESC,
                 CASE WHEN $sort = 3 THEN sale_price_min END ASC,
                 r.outstanding DESC
        LIMIT $pageSize OFFSET $offset;

    ELSE
        -- Lấy tổng số bản ghi
        SELECT COUNT(*)
        INTO $totalRecord
        FROM result3 r
        WHERE r.product_name LIKE CONCAT('%', IFNULL($keyword, ''), '%')
          AND ($fromAmount IS NULL
            OR r.sale_price_max >= $fromAmount)
          AND ($toAmount IS NULL
            OR r.sale_price_min <= $toAmount)
          AND ($rating IS NULL
            OR r.rate >= $rating);
-- Lấy danh sách sản phẩm
        SELECT *,
               $totalRecord AS totalRecord
        FROM result3 r
        WHERE r.product_name LIKE CONCAT('%', IFNULL($keyword, ''), '%')
          AND ($fromAmount IS NULL
            OR r.sale_price_max >= $fromAmount)
          AND ($toAmount IS NULL
            OR r.sale_price_min <= $toAmount)
          AND ($rating IS NULL
            OR r.rate >= $rating)
        ORDER BY CASE WHEN $sort = 1 THEN created_date END DESC,
                 CASE WHEN $sort = 2 THEN count_order END DESC,
                 CASE WHEN $sort = 4 THEN sale_price_max END DESC,
                 CASE WHEN $sort = 3 THEN sale_price_min END ASC,
                 r.outstanding DESC
        LIMIT $pageSize OFFSET $offset;
    END IF;


END
$$

