           IF(u.date_of_birth IS NOT NULL, DAY(u.date_of_birth), NULL)                     AS `day`,
           IF(u.date_of_birth IS NOT NULL, CONCAT('Tháng ', MONTH(u.date_of_birth)), NULL) AS `month`,
